import './App.css';
import { useState } from 'react'
import ColourButton from './components/ColourButton';

function App() {
  const [background, setBackground] = useState(false)

  const toggleClass = () => {
    setBackground(!background);
  };

  return (
    <div className="App">
      <header style={{ backgroundColor: background ? "darkslateblue" : "yellow" }} className="App-header">
        <ColourButton col={"green"} action={toggleClass}/>
      </header>
    </div>
  );
}

export default App;
