import { useState } from 'react'
import "./ColourButtonStyle.css"

export default function ColourButton(props) {

    const [backgroundtoggle, setBackgroundToggle] = useState(false)
    const toggleClass = () => {
      setBackgroundToggle(!backgroundtoggle);
    };

    return (
        <button action={toggleClass}
        onClick={() => props.action(props.col)} style={{backgroundColor: props.col}} className="btn">
        </button>
    )

}
