  const [background, setBackground] = useState("darkslateblue")

  const [colourOptions, setColourOptions] = useState(["red", "green", "yellow", "blue", "orange"])

  const [profile, setProfile] = useState([{name : "person1", age: "age1", location: "location1", image: ""}, {name : "person2", age: "age2", location: "location2", image: ""}, {name : "person3", age: "age3", location: "location3", image: ""}])


  // const [background, setBackground] = useState(false)
