import { BrowserRouter, Routes, Route } from "react-router-dom";
import AllPosts from "./components/AllPosts";
import OnePost from "./components/OnePost";

function App() {
  // const [background, setBackground] = useState(`#FFFFFF`)
  // const [colourOptions, setColourOptions] = useState(["red", "green", "yellow", "blue", "orange"])

  const [backgroundtoggle, setBackgroundToggle] = useState(false)
  const toggleClass = () => {
    setBackgroundToggle(!backgroundtoggle);
  };

  const [backgroundpagetoggle, setBackgroundpageToggle] = useState(false)
  const togglepageClass = () => {
    setBackgroundpageToggle(!backgroundpagetoggle);
  };

  return (
    <div className="App">
      <header style={{ backgroundColor: backgroundpagetoggle ? "transparent" : "red"  }}
      className="App-header">
        <h1 style={{ backgroundColor: backgroundtoggle ? "transparent" : "darkgrey" }}>hey wrld ~~~**~*<br/>
        Welcome to my portfolio!
        </h1>
        <div className="buttons-top"><p className="toggle">Background: </p>{
          colourOptions.map((colour) => {
            return(
              <ColourButton key={colour} action={togglepageClass} col={"darkgrey"}/>
            )
          })
        }
        <br/><p className="toggle">Header background:</p><ModeButton col={"darkgrey"} action={toggleClass}/>
        </div>

        <BrowserRouter>
          <div>
          <Routes>
            <Route component={AllPosts} path="/" exact element={<AllPosts />}/>
            <Route component={OnePost} path="/:slug" element={<OnePost />}/>
          </Routes>
          </div>
        </BrowserRouter>
      </header>
    </div>
  );
}

export default App;
