import './App.css';
import { useState, useEffect } from 'react'
import ColourButton from './components/ColourButton';
import ProfileInfo from './components/ProfileInfo';
import ProfileStatic from './components/ProfileStatic';
import Profile from './model/Profile'
import image from './images/Asset9.png'

function App() {
  const [background, setBackground] = useState("grey")

  const [colourOptions, setColourOptions] = useState(["red", "green", "yellow", "blue", "orange"])

  const [users, setUsers] = useState([
    new Profile("Matt", 25, "Engalnd", image),
    new Profile("Rob", 26, "Wales", image)
  ])

  const [isLoading, setIsLoading] = useState(false);
  const [profiles, setProfile] = useState([])

  const getData = async (url) => {
    const response = await fetch(url)
    const data = await response.json()
    return data.results
  }

  useEffect(() => {
    const fetchData = async () => {
      const data = await getData("https://randomuser.me/api/?results=10");
      setProfile(data);
    }
      fetchData()
      .catch(console.error);;
  }, []);


  const [backgroundtoggle, setBackgroundToggle] = useState(false)
  const toggleClass = () => {
    setBackgroundToggle(!backgroundtoggle);
  };

  return (
    <div className="App">
      <header style={{ backgroundColor: background }}
      className="App-header">
        <h1 style={{ backgroundColor: backgroundtoggle ? "transparent" : "darkgrey" }}>hey wrld ~~~**~*</h1>
        <div className="buttons-top">{
          colourOptions.map((colour) => {
            return(
              <ColourButton key={colour} action={setBackground} col={colour}/>
            )
          })
        }
        <p className="toggle">Header background: <ColourButton col={"darkgrey"} action={toggleClass}/></p>
        </div>

        <dic className="page">
          {users.map((user) => {
            return(
              <ProfileStatic name={user.image} age={user.age} location={user.location} image={user.image}></ProfileStatic>
            )
          })}
        </dic>


        {isLoading ? <p>Loading...</p> : null}
        <div className="page">
        {
          profiles.map((profile) => {
            return(
                <ProfileInfo key={profile.email} profile={profile}/>
            )
          })
        }
        </div>
      </header>
    </div>
  );
}

export default App;
