import './App.css';
import { useState, useEffect } from 'react'
import ColourButton from './components/ColourButton';
import ProfileInfo from './components/ProfileInfo';

function App() {
  const [background, setBackground] = useState("darkslateblue")

  const [colourOptions, setColourOptions] = useState(["red", "green", "yellow", "blue", "orange"])

  const [profiles, setProfile] = useState([])

  useEffect(() => {
    fetch('https://randomuser.me/api/')
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);
        setProfile(data);
      });
  }, []);


  // const [background, setBackground] = useState(false)
  const toggleClass = () => {
    setBackground(!background);
  };

  // profiles = profiles.results

  return (
    <div className="App">
      <header 
      // style={{ backgroundColor: background ? "darkslateblue" : "green" }} 
      style={{ backgroundColor: background }}
      className="App-header">
        <h1>hey wrld ~~~**~*</h1>
        {/* {
          colourOptions.map((colour) => {
            return(
              <ColourButton action={setBackground} col={colour}/>
            )
          })
        } */}
        {
          profiles.map((profile) => {
            return(
              <ProfileInfo profile={profile}/>
            )
          })
        }
        {/* <ColourButton col={"green"} action={toggleClass}/> */}
      </header>
    </div>
  );
}

export default App;
