export default function ColourButton(props) {

    const [backgroundtoggle, setBackgroundToggle] = useState(false)
  const toggleClass = () => {
    setBackgroundToggle(!backgroundtoggle);
  };

    return (
        <button 
        onClick={() => props.action(props.col)} style={{ backgroundColor: backgroundtoggle ? "transparent" : "darkgrey" }} className="btn">
        </button>
    )

}
