import './App.css';
import { useState, useEffect } from 'react'
import ColourButton from './components/ColourButton';
import ModeButton from './components/ColourButton';
// import ProfileInfo from './components/ProfileInfo';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import AllPosts from "./components/AllPosts.jsx";
import OnePost from "./components/OnePost.jsx";

function App() {
  const [background, setBackground] = useState("grey")
  const [colourOptions, setColourOptions] = useState(["red", "green", "yellow", "blue", "orange"])

  const [allPostsData, setAllPosts] = useState(null);

  useEffect(() => {
    sanityClient
      .fetch(
        `*[_type == "post"]{
        title,
        slug,
        mainImage{
          asset->{
          _id,
          url
        }
      }
    }`
      )
      .then((data) => setAllPosts(data))
      .catch(console.error);
  }, []);

  // const [profiles, setProfile] = useState([])

  // const getData = async (url) => {
  //   const response = await fetch(url)
  //   const data = await response.json()
  //   return data.results
  // }

  // useEffect(() => {
  //   const fetchData = async () => {
  //     const data = await getData("https://randomuser.me/api/?results=10");
  //     setProfile(data);
  //   }
  //     fetchData()
  //     .catch(console.error);;
  // }, []);


  const [backgroundtoggle, setBackgroundToggle] = useState(false)
  const toggleClass = () => {
    setBackgroundToggle(!backgroundtoggle);
  };

  return (
    <div className="App">
      <header style={{ backgroundColor: background }}
      className="App-header">
        <h1 style={{ backgroundColor: backgroundtoggle ? "transparent" : "darkgrey" }}>hey wrld ~~~**~*</h1>
        <div className="buttons-top"><p className="toggle">Background: </p>{
          colourOptions.map((colour) => {
            return(
              <ColourButton key={colour} action={setBackground} col={colour}/>
            )
          })
        }
        <br/><p className="toggle">Header background:</p><ModeButton col={"darkgrey"} action={toggleClass}/>
        </div>

        <BrowserRouter>
          <div>
          <Routes>
            <Route component={AllPosts} path="/" exact element={<AllPosts />}/>
            <Route component={OnePost} path="/:slug" element={<OnePost />}/>
          </Routes>
          </div>
        </BrowserRouter>

        {/* <div className="page">
          Portfolio:
        {
          profiles.map((profile) => {
            return(
                <ProfileInfo key={profile.email} profile={profile}/>
            )
          })
        }
        </div> */}
      </header>
    </div>
  );
}

export default App;
