import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import sanityClient from "../client.js";
import BlockContent from "@sanity/block-content-to-react";
import imageUrlBuilder from "@sanity/image-url";

const builder = imageUrlBuilder(sanityClient);
function urlFor(source) {
  return builder.image(source);
}

export default function AllPosts() {
  const [allPostsData, setAllPosts] = useState(null);

  useEffect(() => {
    sanityClient
      .fetch(
        `*[_type == "post"]{
        title,
        slug,
        mainImage{
          asset->{
          _id,
          url}
        },
        secondImage{
          asset->{
          _id,
          url}
        },
        link,
        type,
        year,
        imgOne{
          asset->{
          _id,
          url}
        },
        imgTwo{
          asset->{
          _id,
          url}
        },
        imgThree{
          asset->{
          _id,
          url}
        },
        backgroundImage{
          asset->{
          _id,
          url}
        },
        body
    }`
      )
      .then((data) => setAllPosts(data))
      .catch(console.error);
  }, []);

  return (
    <div>
      <div>
        {allPostsData &&
          allPostsData.map((post, index) => (
            <>
              <img className="backgroundImage" src={post.backgroundImage.asset.url} alt="" />
              <div>
                <Link to={"/" + post.slug.current} key={post.slug.current}>
                  <span key={index}>
                      <h2>{post.title}</h2>
                  </span>
                </Link>
                <BlockContent
                  blocks={post.body}
                />
              </div>
              <img className="mainImage" src={post.mainImage.asset.url} alt="" />
              <img className="secondImage" src={post.secondImage.asset.url} alt="" />
              <img className="imgOne" src={post.imgOne.asset.url} alt="" />
              <img className="imgTwo" src={post.imgTwo.asset.url} alt="" />
              <img className="imgThree" src={post.imgThree.asset.url} alt="" />
              <div className="footer">
                <div><Link to={post.link}>{post.link}</Link></div>
                <div>{post.type}</div>
                <div>{post.year}</div>
              </div>
            </>
          ))}
      </div>
    </div>
  );
}
