import './App.css';
import { useState, useEffect } from 'react'
import ColourButton from './components/ColourButton';
import ProfileInfo from './components/ProfileInfo';
import ProfileStatic from './components/ProfileStatic';
import Profile from './model/Profile'
import image from './images/Asset9.png'

function App() {
  const [background, setBackground] = useState("grey")

  const [colourOptions, setColourOptions] = useState(["red", "green", "yellow", "blue", "orange"])

  const [users, setUsers] = useState([
    new Profile("Ana", 29, "Engalnd", image),
    new Profile("Camila", 34, "USA", image)
  ])

  const [isLoading, setIsLoading] = useState(false);
  const [profiles, setProfile] = useState([])

  const getData = async (url) => {
    const response = await fetch(url)
    const data = await response.json()
    return data.results
  }

  useEffect(() => {
    const fetchData = async () => {
      const data = await getData("https://randomuser.me/api/?results=10");
      setProfile(data);
    }
      fetchData()
      .catch(console.error);;
  }, []);

  const [backgroundtoggle, setBackgroundToggle] = useState(false)
  const toggleClass = () => {
    setBackgroundToggle(!backgroundtoggle);
  };

  return (
    <div className="App">
      <header style={{ backgroundColor: background }}
      className="App-header">
        <h1 style={{ backgroundColor: backgroundtoggle ? "transparent" : "darkgrey" }}>hey wrld ~~~**~*</h1>
        <div className="buttons-top"><p className="toggle">Background: </p>{
          colourOptions.map((colour) => {
            return(
              <ColourButton backgroundtoggle={backgroundtoggle} key={colour} action={setBackground} col={colour}/>
            )
          })
        }
        <br/><p className="toggle">Header background:</p><ColourButton background={background} col={"darkgrey"} action={toggleClass}/>
        </div>

        <dic className="page">
          Static:
          {users.map((user) => {
            return(
              <ProfileStatic name={user.name} age={user.age} location={user.location} image={user.image}/>
            )
          })}
        </dic>

        <div className="page">
          API:
        {
          profiles.map((profile) => {
            return(
                <ProfileInfo key={profile.email} profile={profile}/>
            )
          })
        }
        </div>
      </header>
    </div>
  );
}

export default App;
